INSERT INTO `uye_ziyaretleri` VALUES ('5', 'Üye Ziyaretleri -REYİL - Reyhan Gürbay', '', '<div>Değerli &Uuml;yemiz,\r\n<div>&nbsp;</div>\r\n<d iv=\"\"></d><d iv=\"\"></d>\r\n<div>&nbsp;</div>\r\n<div><d iv=\"\"></d></div>\r\n<div>Hızla &uuml;ye sayısı artan BUİKAD, &uuml;yelerinin birbirlerine kaynaşmaları ve iş birliklerinin artması i&ccedil;in başlattığı &quot; Cumartesi Biz Bize&quot; sohbetlerinin yanında bu aydan itibaren &uuml;yelerini iş yerlerinde ziyaret etmeye başlıyor.Derneğin vizyonu ve misyonu hakkında bilgi verilirken,yapılan ve planlanan etkinlikler hakkında da &uuml;yeler bilgilendiriliyor.&nbsp;&nbsp; <d iv=\"\"></d></div>\r\n<div>&nbsp;</div>\r\n<d iv=\"\"></d><d iv=\"\"></d>\r\n<div>&nbsp;</div>\r\n<d iv=\"\"></d>İlk &uuml;ye ziyaretimizi &nbsp;REYİL firması sahibi Sn. Reyhan G&uuml;rbay`a ger&ccedil;ekleştirdik. <d iv=\"\"></d>\r\n<div>&nbsp;</div>\r\n<d iv=\"\"></d><d iv=\"\"></d>\r\n<div>&nbsp;</div>\r\n<d iv=\"\"></d>Y&ouml;netim Kurulu &Uuml;yelerimizden Sn. G&uuml;lşah Ağralı G&uuml;ven ve Sn. Semra Baykan tarafından yapılan ziyarette Reyhan Hn`a BUİKAD plaketi de sunuldu. <d iv=\"\"></d></div>', '2013-07-17', '1374689335img_4522.jpg'), ('6', 'Üye Ziyaretleri-ATA ISI -Nermin Atalay', '', '<div>&nbsp;&Uuml;ye ziyaretlerimizin ikincisini, &uuml;yemiz ve Genel Sekreterimiz ve ATA ISI Y&ouml;netim Kurulu Başkanı &nbsp;Sn. Nermin Atalay`a yaptık.</div>\r\n<div>&nbsp;</div>\r\n<div>Y&ouml;netim Kurulu &uuml;yelerimizden Sn. Ulviye Yahya, Sn. Seda &Ouml;zmutlu ve Sn. Semra Baykan tarafından ger&ccedil;ekleştirilen ziyarette,keyifli sohbetin yanısıra, derneğin yeni d&ouml;nem planları, projeler hakkında da g&ouml;r&uuml;ş&uuml;ld&uuml;.</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>', '2013-07-19', '1374743816adsiz.jpg'), ('7', 'Üye Ziyaretleri- SGN KİMYA - Sudan Kurtoğlu', '', '<div>&nbsp;3. &Uuml;ye ziyaretimizi SGN Kimya- Sn. Sudan Kurtoğlu`na ger&ccedil;ekleştirdik. 15 Ağustos perşembe g&uuml;n&uuml; Y&ouml;netim Kurulu &Uuml;yelerimiz Sn. İpek Yal&ccedil;ın ve Sn. Semra Baykan tarafından ger&ccedil;ekleştirilen ziyarette, derneğimizin yeni d&ouml;nem planları, hedefleri &uuml;zerinde konuşuldu.&nbsp;</div>\r\n<div>Sudan Hn ile &nbsp;dernek ile ilgili fikir alışverişinde bulunuldu.</div>', '2013-08-15', '1376655983img_2540.jpg'), ('8', 'Üye Ziyaretleri-GÖKÇEN OPTİK- Elif Ahsen Gökçen', '', '<div>&nbsp;&Uuml;ye ziyaretlerimiz kapsamında bug&uuml;n G&ouml;k&ccedil;en Optik ortağı değerli &uuml;yemiz Elif Ahsen G&ouml;k&ccedil;en&rsquo;i ziyaret ettik.</div>\r\n<p class=\"MsoNormal\"><o:p></o:p></p>\r\n<div><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><d iv=\"\">Y&ouml;netim kurulu &uuml;yelerimiz İpek Yal&ccedil;ın, Semra Baykan ve Nermin Atalay ile birlikte &nbsp;Elif Ahsen G&ouml;k&ccedil;en &lsquo;i ziyaret edip <o:p></o:p></d><d iv=\"\">&uuml;yelik plaketini takdim ettik.Sıcak karşılaması ve g&uuml;zel sohbeti i&ccedil;in Ahsen hanıma teşekk&uuml;r ederiz.&nbsp;<o:p></o:p></d></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">    <d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></div>', '2013-08-20', '1377071401dsc_1060.jpg'), ('9', '', '', '', '0000-00-00', ''), ('10', 'Üye Ziyaretleri- BAYRAK PLASTİK- Gülçin Bayrak & Nagehan Bayrak', '', '<div>&nbsp;&Uuml;ye Ziyaretlerimizin 5.sini Bayrak Plastik Y&ouml;netim Kurulu &Uuml;yelerinden G&uuml;l&ccedil;in ve Nagehan Bayrak`a ger&ccedil;ekleştirdik.</div>', '2013-09-11', '1379071845012.jpg'), ('11', 'Üye Ziyaretleri- Ortodondist- Zehra Bekler', '', '<div>&nbsp;&Uuml;ye ziyaretlerimizin 6. sını Ortodontist Sn. Zehra Bekler`e ger&ccedil;ekleştirdik.</div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\">Ziyaretiniz beni fazlasıyla memnun etti. Buikad &uuml;yesi olarak diğer &uuml;yelerle birebir sohbet etme imkanı her zaman m&uuml;mk&uuml;n olamıyordu. </span></strong><o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\"><d iv=\"\">Gerek islerimin yoğunluğu, gerekse &ccedil;alışma saatlerim nedeniyle arzu ettiğim oranda toplantılara katılamıyordum. Bu nedenle de siz değerli Buikad &uuml;yeleri ile</d></span></strong> <o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\"><d iv=\"\">birebir g&ouml;r&uuml;şerek hem sizleri tanımak hem de kendimi tanıtma imkanı yeterince bulamamıştım. Bu nedenle ziyaretiniz, sıcak sohbetiniz beni fazlasıyla memnun etti.</d></span></strong> <o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\"><d iv=\"\">Ayrıca Cumartesi g&uuml;nleri d&uuml;zenlediğiniz Biz bize sohbetlerini de &ccedil;ok beğenerek katılmaya &ccedil;alışıyorum.&nbsp; Hem farklı meslek grubundaki &uuml;yeleriniz ile tanışmak, hem de</d></span></strong> <o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\"><d iv=\"\">farklı&nbsp; mesleklere veya hobilere dair bilgiler edinmek, ayrıca g&uuml;zel bir kahvaltı eşliğinde Buikad &uuml;yeleri ile sohbet ederek bilgi paylaşımında bulunmak, benim i&ccedil;in b&uuml;y&uuml;k </d></span></strong><o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\"><d iv=\"\">keyif oldu. Bu tip organizasyonların devamını dileyerek, bu derneğe &uuml;ye olmaktan duyduğum sevinci dile getirmek istiyorum. </d></span></strong><o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><strong><span style=\"mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#646B86\"><d iv=\"\">&Ccedil;ok teşekk&uuml;rler ve iyi &ccedil;alışmalar diliyorum.Sevgilerimle <u>Zehra Bekler</u></d></span></strong> <o:p></o:p></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"><d iv=\"\"></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></d></div>', '2013-09-11', '1379072174zehra_bekler._beraber.jpg'), ('12', 'Üye Ziyaretleri-Renault -Jale Kurt', '', '<div>&nbsp;&Uuml;ye Ziyaretlerimizin 7.sini Renault firmasında Y&ouml;neticilerinden Sn. Jale Kurt`a ger&ccedil;ekleştirdik.</div>\r\n<div>Jale Hn`ın ziyaretimiz sonrası duygularını aşağıd bulabilirsiniz.</div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<p class=\"MsoNormal\"><span lang=\"TR\">&Ouml;ncelikle ziyaretiniz i&ccedil;in &ccedil;ok teşekk&uuml;r ederim , plaket beni &ccedil;ok mutlu etti. BUIKAD derneğinin &uuml;yesi olmak beni bir kez daha gururlandırdı. BUIKAD &lsquo;ın y&uuml;r&uuml;tt&uuml;ğ&uuml; projelerin her birinin &ccedil;ok değerli olduğunu , ulaşabildiği kişiler a&ccedil;ısından &ccedil;ok b&uuml;y&uuml;k katma değer yarattığını d&uuml;ş&uuml;n&uuml;yorum. Dernek i&ccedil;inde yapılan faaliyetler de birbirinden faydalı ve eğlenceli. Bu faaliyetler sayesinde hem &nbsp;ufkumuz a&ccedil;ılıyor, hem g&uuml;zel ve nezih insanlarla fikir paylaşımı ve iletişim i&ccedil;inde bulunmuş oluyoruz. BUIKAD &lsquo;a &uuml;ye olmaktan hem gurur duyuyorum hem de buradaki paylaşımlar beni &ccedil;ok mutlu ediyor. Daha nice g&uuml;zel projelerde yer almak dileği ile.<o:p></o:p></span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span lang=\"TR\">Sevgi ve Saygılarımla<o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"TR\">Jale Kurt<o:p></o:p></span></p>\r\n</div>', '2013-09-13', '1379434011jale_kurt.jpg'), ('13', 'Üye Ziyaretleri- Cemikids-Nermin Ceyhan', '', '<div>&nbsp;&Uuml;ye ziyaretlerimizin 8. sini Cemikids sahibi Sn. Nermin Ceyhan`a ger&ccedil;ekleştirdik.</div>', '2013-09-17', '1380294506nermin_ceyhan_2.jpg'), ('14', 'Üye Ziyaretleri-Prosistem Bilgisayar-Ayla Soygür', '', '<div>&nbsp;&Uuml;ye ziyaretlerinin 9.sunu Prosistem Kurucu Ortaklarından Sn. Ayla Soyg&uuml;r`e ger&ccedil;ekleştirdik.</div>', '2013-09-17', '1379610292ayla_soygur9.jpg'), ('15', 'Üye Ziyaretleri-Sedef Sinop-Fleksit Mobilya', '', '<div>&nbsp;Derneğimizin yeni &uuml;yelerinden <strong>Sn.Sedef Sinoplu</strong>&rsquo;yu işyerinde ziyaret ettik. Sedef Sinoplu <strong>Fleks/Sit</strong> Ofis mobilyalarının Bursa Bayisi ve kadın girişimcilerimizdendir.</div>\r\n<p class=\"MsoNormal\"><o:p></o:p></p>\r\n<div><d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\"><d iv=\"\">Bizlere g&ouml;sterdiği sıcak ilgi ve nazik hediyeleri i&ccedil;in &ccedil;ok teşekk&uuml;r ediyoruz.Sedef hanım derneğimiz hakkındaki d&uuml;ş&uuml;ncelerini de bizler ile paylaşmış. <o:p></o:p></d></p>\r\n<d iv=\"\"><d iv=\"\"><d iv=\"\">\r\n<p class=\"MsoNormal\">&nbsp;<o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size:11.0pt;color:#333333\">Değerli Başkanım ve y&ouml;netim kurulundaki Arkadaşlarım;</span><o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size:11.0pt;color:#333333\">Ziyaretiniz ve İlk almış olduğum BUİKAD Plaketim beni &ccedil;ok mutlu etti,Teşekk&uuml;r ederim.Girişimci olmaya karar verdiğimde ilk hedefim uzun zamandan beri takip ettiğim BUİKAD &Uuml;yesi olmaktı.</span><o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size:11.0pt;color:#333333\">Benim Yıldıza Ulaşmak hedefimi ve hayalimi ger&ccedil;ekleştirmeme yardımcı oldunuz.Girişimciliğimde sizinle Birlikte kendimi daha da geliştirdim,&Ccedil;ok değerli hepsi birbirinden &ouml;nemli arkadaşlarım,dostlarım oldu.</span><o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size:11.0pt;color:#333333\">Aranızda olmaktan,bu yolda birlikte y&uuml;r&uuml;mekten dolayı &ccedil;ok gururluyum.</span><o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size:11.0pt;color:#333333\">Saygılarımla</span><o:p></o:p></p>\r\n<p class=\"MsoNormal\">&nbsp;<o:p></o:p></p>\r\n<p class=\"MsoNormal\">&nbsp;<o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size:11.0pt;color:#333333\">İyi</span><span style=\"color:#333333\"> &Ccedil;alışmalar...</span><o:p></o:p></p>\r\n<p class=\"MsoNormal\"><span style=\"color:#333333\">Sedef Sinoplu</span><o:p></o:p></p>\r\n</d></d></d></d></d></d></div>', '2013-09-21', '1380214954bu14.jpg'), ('16', 'Üye Ziyaretleri-Emine Örnek-Özel Emine Örnek Okulları', '', '<h3><span style=\"font-family: Arial;\">&Uuml;ye ziyaretlerimiz kapsamında derneğimizin değerli &uuml;yesi <strong>Sn.Emine &Ouml;rnek</strong>&rsquo;i ziyaret ettik. Emine &Ouml;rnek Bursa&rsquo;nın k&ouml;kl&uuml; markası <strong>Emine &Ouml;rnek</strong> okullarının kurucusu </span><o:p></o:p></h3>\r\n<h3><d iv=\"\"><d iv=\"\"><d iv=\"\"><span style=\"font-family: Arial;\"><span style=\"font-size: 12pt;\">ve Y&ouml;netim Kurulu Başkanıdır.Y&ouml;netim kurulu olarak ger&ccedil;ekleştirdiğimiz ziyaretimizde bizlere g&ouml;sterdiği konukseverlik i&ccedil;in kendisine &ccedil;ok teşekk&uuml;r ediyoruz</span></span></d></d></d><span style=\"font-family: Arial;\">&nbsp;</span></h3>', '2013-09-21', '1380215146buikad_resim_(1).jpg'), ('17', 'Üye Ziyaretleri-Ebru Yangöz Sağlık-Yangöz Oto Kurtarma', '', '<div>&nbsp;<strong>Yang&ouml;z Oto</strong> Kurtarma firması ortağı ve y&ouml;neticisi <em><b>Ebru Yang&ouml;z Sağlık</b></em> &lsquo;a Y&ouml;netim kurulu &uuml;yelerimizden Nermin Atalay ve G&uuml;lşah Ağralı G&uuml;ven</div>\r\n<p class=\"MsoNormal\"><o:p></o:p></p>\r\n<div><span style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;;mso-ansi-language:EN-US;mso-fareast-language:EN-US;\r\nmso-bidi-language:AR-SA\">&uuml;yelik plaketini teslim etti. İş yerinde bizi g&uuml;ler g&uuml;z&uuml; ile ağırlayan değerli &uuml;yemize teşekk&uuml;r ederiz</span></div>', '2013-09-24', '1380215260bu1.jpg'), ('18', 'Üye Ziyaretleri-Duygu Özbekçi-KuterPR', '', '<div>&nbsp;<strong>Kuter PR</strong> ortağı ve y&ouml;neticisi <em><b>Duygu &Ouml;zbek&ccedil;i&rsquo;</b></em> ye BUİKAD &uuml;yelik plaketini takdim ettik. Duygu hanımın hoş sohbeti i&ccedil;in teşekk&uuml;r ederiz.&nbsp;</div>\r\n<p class=\"MsoNormal\"><o:p></o:p></p>\r\n<div><d iv=\"\"><d iv=\"\"></d></d></div>', '2013-09-24', '1380215344bu4.jpg'), ('19', 'Üye Ziyaretleri-Nur Banu Öncel / Fizmer ', '', '<div>&nbsp;<span style=\"FONT-FAMILY: `Comic Sans MS`; FONT-SIZE: 10pt\" lang=\"TR\">Bu hafta  <b>Fizmer Lazer ve Estetik Merkezi </b>sahibi &uuml;yemiz, Lazer Medikal Belgeli  <b>Uzm.Dr.Nur Banu &Ouml;ncel&rsquo;i ziyaret ettik</b></span></div>', '2013-10-09', '1381568275banu_oncel.jpg'), ('20', 'Üye Ziyaretleri-Sevgi Sezer / SOYKİM', '', '<div>&nbsp;<